import 'package:flutter/material.dart';

void main() {
  runApp(JapaneseMenuApp());
}

class JapaneseMenuApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Cardápio Japonês',
      theme: ThemeData(
        primarySwatch: Colors.red,
        useMaterial3: true,
      ),
      home: MenuHomePage(),
    );
  }
}

class MenuHomePage extends StatefulWidget {
  @override
  _MenuHomePageState createState() => _MenuHomePageState();
}

class _MenuHomePageState extends State<MenuHomePage> {
  final List<Dish> dishes = [
    Dish(
      name: 'Sushi Especial',
      description: 'Delicioso sushi com salmão, atum e abacate. Preço: 8 reais a peça.',
      imageUrl: 'data:image/jpeg;base64,',
      category: 'Sushi',
      nutrition: {'Calorias': '200', 'Proteínas': '15g', 'Carboidratos': '30g'},
    ),
    Dish(
      name: 'Ramen Tradicional',
      description: 'Sopa de macarrão com caldo de porco e vegetais. Preço: 47 reais.',
      imageUrl: 'data:image/jpeg;base64,',
      category: 'Ramen',
      nutrition: {'Calorias': '500', 'Proteínas': '20g', 'Carboidratos': '60g'},
    ),
    Dish(
      name: 'Tempura de Camarão',
      description: 'Camarões empanados e fritos, servidos com molho especial. Preço: 36 reais com 6 unidades.',
      imageUrl: 'data:image/jpeg;base64,',
      category: 'Tempura',
      nutrition: {'Calorias': '300', 'Proteínas': '25g', 'Carboidratos': '40g'},
    ),
    Dish(
      name: 'Mochi de Morango',
      description: 'Sobremesa tradicional japonesa feita de arroz e recheada com morango. Preço: 25 reais.',
      imageUrl: 'data:image/jpeg;base64,',
      category: 'Sobremesas',
      nutrition: {'Calorias': '150', 'Proteínas': '2g', 'Carboidratos': '35g'},
    ),
  ];

  String selectedCategory = 'Todos';

  @override
  Widget build(BuildContext context) {
    List<Dish> filteredDishes = selectedCategory == 'Todos'
        ? dishes
        : dishes.where((dish) => dish.category == selectedCategory).toList();

    return Scaffold(
      appBar: PreferredSize(
        preferredSize: const Size.fromHeight(80.0),
        child: ClipRRect(
          borderRadius: const BorderRadius.vertical(bottom: Radius.circular(48)),
          child: AppBar(
            title: FittedBox(
              fit: BoxFit.scaleDown,
              child: Row(
                children: const [
                  Icon(Icons.set_meal, size: 32, color: Colors.white),
                  SizedBox(width: 8),
                  Text(
                    'TóquioWasabi',
                    style: TextStyle(fontSize: 22, fontWeight: FontWeight.bold, color: Colors.white),
                  ),
                ],
              ),
            ),
            backgroundColor: Colors.red,
            centerTitle: true,
          ),
        ),
      ),
      body: Column(
        children: [
          Padding(
            padding: const EdgeInsets.all(16.0),
            child: Text(
              'Escolha o seu prato!',
              style: TextStyle(fontSize: 18, fontWeight: FontWeight.bold, color: Colors.red),
              textAlign: TextAlign.center,
            ),
          ),
          Wrap(
            spacing: 8.0,
            runSpacing: 4.0,
            children: [
              ChoiceChip(
                label: Text('Todos'),
                selected: selectedCategory == 'Todos',
                onSelected: (bool selected) {
                  setState(() {
                    selectedCategory = 'Todos';
                  });
                },
              ),
              ChoiceChip(
                label: Text('Sushi'),
                selected: selectedCategory == 'Sushi',
                onSelected: (bool selected) {
                  setState(() {
                    selectedCategory = 'Sushi';
                  });
                },
              ),
              ChoiceChip(
                label: Text('Ramen'),
                selected: selectedCategory == 'Ramen',
                onSelected: (bool selected) {
                  setState(() {
                    selectedCategory = 'Ramen';
                  });
                },
              ),
              ChoiceChip(
                label: Text('Tempura'),
                selected: selectedCategory == 'Tempura',
                onSelected: (bool selected) {
                  setState(() {
                    selectedCategory = 'Tempura';
                  });
                },
              ),
              ChoiceChip(
                label: Text('Sobremesas'),
                selected: selectedCategory == 'Sobremesas',
                onSelected: (bool selected) {
                  setState(() {
                    selectedCategory = 'Sobremesas';
                  });
                },
              ),
            ],
          ),
          Expanded(
            child: ListView.builder(
              padding: const EdgeInsets.all(16),
              itemCount: filteredDishes.length,
              itemBuilder: (context, index) {
                return Padding(
                  padding: const EdgeInsets.only(bottom: 16),
                  child: DishCard(dish: filteredDishes[index]),
                );
              },
            ),
          ),
        ],
      ),
    );
  }
}

class DishCard extends StatelessWidget {
  final Dish dish;

  const DishCard({required this.dish});

  @override
  Widget build(BuildContext context) {
    return Stack(
      children: [
        Container(
          width: double.infinity,
          decoration: BoxDecoration(
            color: Colors.lightBlue[50],
            borderRadius: BorderRadius.circular(12),
            boxShadow: const [
              BoxShadow(color: Colors.black26, blurRadius: 4, offset: Offset(2, 2)),
            ],
          ),
          child: Column(
            crossAxisAlignment: CrossAxisAlignment.center,
            children: [
              ClipRRect(
                borderRadius: const BorderRadius.vertical(top: Radius.circular(12)),
                child: Image.network(
                  dish.imageUrl,
                  width: double.infinity,
                  height: 200,
                  fit: BoxFit.cover,
                ),
              ),
              Padding(
                padding: const EdgeInsets.all(16),
                child: Column(
                  crossAxisAlignment: CrossAxisAlignment.center,
                  children: [
                    Text(
                      dish.name,
                      style: const TextStyle(fontSize: 20, fontWeight: FontWeight.bold),
                    ),
                    const SizedBox(height: 8),
                    Text(
                      dish.description,
                      style: const TextStyle(fontSize: 16),
                      textAlign: TextAlign.center,
                    ),
                    const SizedBox(height: 8),
                    ElevatedButton(
                      style: ElevatedButton.styleFrom(
                        backgroundColor: Colors.red,
                        foregroundColor: Colors.white,
                      ),
                      onPressed: () {
                        _showOrderStatus(context);
                      },
                      child: const Text('Pedir'),
                    ),
                  ],
                ),
              ),
            ],
          ),
        ),
        Positioned(
          top: 12,
          right: 12,
          child: Container(
            padding: const EdgeInsets.symmetric(horizontal: 8, vertical: 4),
            decoration: BoxDecoration(
              color: Colors.green,
              borderRadius: BorderRadius.circular(6),
            ),
            child: const Text('Desconto', style: TextStyle(color: Colors.white, fontSize: 12)),
          ),
        ),
      ],
    );
  }

  void _showOrderStatus(BuildContext context) {
    showDialog(
      context: context,
      builder: (BuildContext context) {
        return AlertDialog(
          title: const Text('Pedido Feito!'),
          content: Column(
            mainAxisSize: MainAxisSize.min,
            children: [
              Text('Seu pedido está sendo preparado.'),
              const SizedBox(height: 8),
              Table(
                children: dish.nutrition.entries.map((entry) {
                  return TableRow(
                    children: [
                      Padding(
                        padding: const EdgeInsets.all(8.0),
                        child: Text(entry.key, style: const TextStyle(fontWeight: FontWeight.bold)),
                      ),
                      Padding(
                        padding: const EdgeInsets.all(8.0),
                        child: Text(entry.value),
                      ),
                    ],
                  );
                }).toList(),
              ),
              const SizedBox(height: 8),
              const CircularProgressIndicator(),
            ],
          ),
          actions: [
            TextButton(
              onPressed: () {
                Navigator.of(context).pop();
              },
              child: const Text('OK'),
            ),
          ],
        );
      },
    );
  }
}

class Dish {
  final String name;
  final String description;
  final String imageUrl;
  final String category;
  final Map<String, String> nutrition;

  Dish({required this.name, required this.description, required this.imageUrl, required this.category, required this.nutrition});
}
